import { Outcome } from '@serenity-js/core/lib/model';
import { Test } from 'mocha';

/**
 * @package
 */
export class OutcomeRecorder {
    private recordedOutcomes: RecordedOutcome[] = [];

    public started(test: Test): void {
        this.recordedOutcomes.push({ test });
    }

    public finished(test: Test, outcome: Outcome): void {
        this.recordedOutcomes = this.recordedOutcomes.map(recordedOutcome =>
            recordedOutcome.test === test
                ? {...recordedOutcome, outcome }
                : recordedOutcome
        )
    }

    public outcomeOf(test: Test): Outcome {
        return this.recordedOutcomes.find(recordedOutcome => recordedOutcome.test === test)?.outcome;
    }

    public erase(test: Test): void {
        this.recordedOutcomes = this.recordedOutcomes.filter(recordedOutcome => recordedOutcome.test !== test);
    }
}

interface RecordedOutcome {
    test: Test;
    outcome?: Outcome;
}

import { Serenity } from '@serenity-js/core';
import { DomainEvent, SceneFinished, SceneFinishes, SceneStarts, SceneTagged, TestRunFinished, TestRunFinishes, TestRunnerDetected } from '@serenity-js/core/lib/events';
import { FeatureTag, Name } from '@serenity-js/core/lib/model';
import { MochaOptions, reporters, Runner, Test } from 'mocha';
import { MochaOutcomeMapper, MochaTestMapper } from './mappers';
import { OutcomeRecorder } from './OutcomeRecorder';

export class SerenityReporterForMocha extends reporters.Base {

    private readonly testMapper: MochaTestMapper = new MochaTestMapper();
    private readonly outcomeMapper: MochaOutcomeMapper = new MochaOutcomeMapper();

    private readonly recorder: OutcomeRecorder = new OutcomeRecorder();

    /**
     * @param {Serenity} serenity
     * @param {mocha~Runner} runner
     * @param {mocha~MochaOptions} options
     */
    constructor(private readonly serenity: Serenity,
                runner: Runner,
                options?: MochaOptions,
    ) {
        super(runner, options);

        runner.on(Runner.constants.EVENT_TEST_BEGIN,
            (test: Test) => {
                this.recorder.started(test);

                this.announceSceneStartsFor(test);
            },
        );

        runner.on(Runner.constants.EVENT_TEST_PASS,
            (test: Test) => {
                this.recorder.finished(test.ctx.currentTest || test, this.outcomeMapper.outcomeOf(test))
            },
        );

        runner.on(Runner.constants.EVENT_TEST_FAIL,
            (test: Test, err: Error) => {
                this.recorder.finished(test.ctx.currentTest || test, this.outcomeMapper.outcomeOf(test))
            },
        );

        const announceSceneFinishedFor = this.announceSceneFinishedFor.bind(this);

        runner.suite.afterEach('Serenity/JS', function () {
            return announceSceneFinishedFor(this.currentTest);
        });

        // Tests without body don't trigger the above custom afterEach hook
        runner.on(Runner.constants.EVENT_TEST_PENDING,
            (test: Test) => {
                if (! test.fn) {
                    this.announceSceneSkippedFor(test);
                }
            },
        );
    }

    public done(failures: number, fn?: (failures: number) => void): void {
        this.emit(new TestRunFinishes(this.serenity.currentTime()));

        this.serenity.waitForNextCue()
            .then(() => this.emit(new TestRunFinished(this.serenity.currentTime())))
            .then(() => fn(failures));
    }

    private announceSceneStartsFor(test: Test): void {
        const scenario = this.testMapper.detailsOf(test);

        this.emit(
            new SceneStarts(
                scenario,
                this.serenity.currentTime(),
            ),
            new SceneTagged(
                scenario,
                new FeatureTag(this.testMapper.featureNameFor(test)),
                this.serenity.currentTime(),
            ),
            new TestRunnerDetected(
                new Name('Mocha'),
                this.serenity.currentTime(),
            ),
        );
    }

    private announceSceneFinishedFor(test: Test): Promise<void> {
        const scenario = this.testMapper.detailsOf(test);

        this.emit(
            new SceneFinishes(
                scenario,
                this.serenity.currentTime(),
            ),
        );

        return this.serenity.waitForNextCue()
            .then(() => {
                this.emit(new SceneFinished(
                    scenario,
                    this.recorder.outcomeOf(test) || this.outcomeMapper.outcomeOf(test),
                    this.serenity.currentTime(),
                ));

                this.recorder.erase(test);
            });
    }

    private announceSceneSkippedFor(test: Test): void {
        const scenario = this.testMapper.detailsOf(test)

        this.announceSceneStartsFor(test);

        this.emit(
            new SceneFinishes(
                scenario,
                this.serenity.currentTime(),
            ),
            new SceneFinished(
                scenario,
                this.outcomeMapper.outcomeOf(test),
                this.serenity.currentTime(),
            )
        );
    }

    private emit(...events: DomainEvent[]): void {
        events.forEach(event => this.serenity.announce(event));
    }
}

import { serenity } from '@serenity-js/core';
import { MochaOptions, Runner } from 'mocha';
import { SerenityReporterForMocha } from './SerenityReporterForMocha';

export = function (runner: Runner, options?: MochaOptions) {
    return new SerenityReporterForMocha(serenity, runner, options);
}

import { AssertionError, ImplementationPendingError, TestCompromisedError } from '@serenity-js/core';
import {
    ExecutionCompromised,
    ExecutionFailedWithAssertionError,
    ExecutionFailedWithError,
    ExecutionSkipped,
    ExecutionSuccessful,
    ImplementationPending,
    Outcome,
} from '@serenity-js/core/lib/model';
import { Test } from 'mocha';

/**
 * @package
 */
export class MochaOutcomeMapper {
    public outcomeOf(test: Test): Outcome {
        switch (true) {
            case !! test.err && this.isAssertionError(test.err):
                return new ExecutionFailedWithAssertionError(test.err as AssertionError);

            case !! test.err && test.err instanceof TestCompromisedError:
                return new ExecutionCompromised(test.err as TestCompromisedError);

            case !! test.err:
                return new ExecutionFailedWithError(test.err);

            case test.isPending() && !! test.fn:
                return new ExecutionSkipped();

            case test.isPending() && ! test.fn:
                return new ImplementationPending(new ImplementationPendingError(`Scenario not implemented`));

            default:
                return new ExecutionSuccessful();
        }
    }

    private isAssertionError(error: Error): error is AssertionError {
        return error instanceof AssertionError
            || this.looksLikeAnAssertionError(error);
    }

    private looksLikeAnAssertionError(error: Error): error is AssertionError {
        return /^AssertionError/.test(error.name)
            && error.hasOwnProperty('expected')
            && error.hasOwnProperty('actual')
    }
}

import { FileSystemLocation, Path } from '@serenity-js/core/lib/io';
import { Category, Name, ScenarioDetails } from '@serenity-js/core/lib/model';
import { Suite, Test } from 'mocha';

/**
 * @package
 */
export class MochaTestMapper {
    public detailsOf(test: Test): ScenarioDetails {

        function fileOf(t) {
            return !! t.file || ! t.parent
                ? t.file
                : fileOf(t.parent);
        }

        return new ScenarioDetails(
            new Name(this.nameOf(test)),
            new Category(this.featureNameFor(test)),
            new FileSystemLocation(
                new Path(fileOf(test)),
            ),
        );
    }

    public featureNameFor(scenario: Test | Suite): string {
        const parentTitle = scenario?.parent?.title;

        return parentTitle !== undefined && parentTitle.trim() !== ''
            ? this.featureNameFor(scenario.parent)
            : scenario.title;
    }

    private fullNameOf(scenario: Test | Suite): string {
        return !! scenario.parent
            ? `${ this.fullNameOf(scenario.parent) } ${ scenario.title }`.trim()
            : scenario.title;
    }

    private nameOf(scenario: Test | Suite): string {
        return this.fullNameOf(scenario)
            .substring(this.featureNameFor(scenario).length)
            .trim();
    }
}

